import React, { useState } from 'react';
import { useQuery } from '@apollo/react-hooks';
import { getAuthorsQuery } from '../queries/queries';

function AddBook() {
  const { loading, error, data } = useQuery(getAuthorsQuery);

  const [values, setValues] = useState({
    name: '',
    genre: '',
    author: ''
  });

  const handleChange = () => {};

  return (
    <div>
      <form id='add-book' onSubmit={handleSubmit}>
        <div className='field'>
          <label>Book name:</label>
          <input type='text' value={values.name} onChange={handleChange} />
        </div>
        <div className='field'>
          <label>Genre:</label>
          <input type='text' value={values.genre} onChange={handleChange} />
        </div>
        <div className='field'>
          <label>Author:</label>
          <select value={values.author} onChange={handleChange}>
            <option>Select Author</option>
            {loading && <option>Loading authors ...</option>}
            {error && <option>Error in loading authors...</option>}
            {data &&
              data.authors.map(author => (
                <option key={author.id} value={author.id}>
                  {author.name}
                </option>
              ))}
          </select>
        </div>
        <div className='field'>
          <button type='submit'>+</button>
        </div>
      </form>
    </div>
  );
}

export default AddBook;
